<?php# @uthor armando_rdz, at 09/04/20    require '../db/Database.php';    $db = new Database();    $con = $db->connect();    $id = $_POST['id'];    $sql = "SELECT j.id 'id', j.titulo 'titulo',                j.dir_img 'pathimg', i.fecha 'instalacion'            FROM consolas c            LEFT JOIN instalaciones i ON c.id = i.id_consola            LEFT JOIN juegos j ON i.id_juego = j.id            WHERE c.id = '$id'            ORDER BY i.fecha DESC";    $result = mysqli_query($con, $sql);    /**     * Recorrer resultado de la consulta para retornarlos en forma de JSON     */    $i = 0; # contador de fila    while ($row = mysqli_fetch_row($result)){        $data[$i]["id"] = $row[0];        $data[$i]["titulo"] = $row[1];        $data[$i]["pathimg"] = $row[2];        $data[$i]["instalacion"] = $row[3];        $i += 1;    }    /**     * @return $data en JSON     */    echo json_encode($data);?>